     $(document).ready(function(){
         $('#data').after('<div id="right-col"></div>');
         var rowsShown = 5;
         var rowsTotal = $('#data tbody tr').length;
         //alert("rowsTotal="+rowsTotal);
         var numPages = Math.floor(rowsTotal/rowsShown);

         $('#right-col').append('<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;');
         $('#right-col').append('&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;');
         $('#right-col').append('&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;');
         for(i = 0;i < numPages;i++) {
             var pageNum = i + 1;
             $('#right-col').append('<a href="#" rel="'+i+'">'+pageNum+'</a> ');
         }
         $('#data tbody tr').hide();
         $('#data tbody tr').slice(0, rowsShown).show();
         $('#right-col a:first').addClass('active');
         $('#right-col a').bind('click', function(){

             $('#right-col a').removeClass('active');
             $(this).addClass('active');
             var currPage = $(this).attr('rel');
             var startItem = currPage * rowsShown;
             var endItem = startItem + rowsShown;
             $('#data tbody tr').css('opacity','0.0').hide().slice(startItem, endItem).
                     css('display','table-row').animate({opacity:1}, 300);
         });
     });
 </script>



