function carts1(url1){
    $.ajax({
   		cache: false,
           url:url1,
           dataType: 'json',
           success: function(data){  
           console.log(data.cart.allItems);

           if(data.cart.allItems.length != 0){
           	   $('#cartBody').html('');

           	    var value=(data.dPercent[0])*100;
           	    var totalPriceAfterDiscount=(data.dPercent[0])*(data.cart.total);
  	  	        totalPriceAfterDiscount = data.cart.total - totalPriceAfterDiscount;
           	     //alert(data.cart.allItems.length);
           		$('#itemValue').append("("+data.cart.allItems.length+" items)");	

            	//Price val in header
            	//alert(data.cart.total);
  	  	  		$('#headerPrice').html('');
  	  	  		$('#headerPrice').append("$"+totalPriceAfterDiscount);  
           	  }else{
           		$('#itemValue').append("(0 items)");
           	 //Price val in header
      	  	  $('#headerPrice').html('');
      	  	  $('#headerPrice').append("$ 0.0");
           	  }

            },
   	});


   }


 $(document).ready(function(){ 	
     	    for(var year = 2014 ; year >=2000; year--){
     	    	$('#publishDateFrom').append("<option value='"+year+"'>"+year+"</option>");
               } 
   	         for(var year = 2015 ; year >=2000; year--){
   	    	     $('#publishDateTo').append("<option value='"+year+"'>"+year+"</option>");
             }
   	        for(var price = 0 ; price<=1000; price++){
   	    	    $('#minPrice').append("<option value='"+price+"'>"+price+"</option>");
   	    	    price=price+99;
             } 
 	         for(var price = 1000 ; price >= 0; price--){
 	    	     $('#maxPrice').append("<option value='"+price+"'>"+price+"</option>");
 	    	     price=price-99;
             }
     	     var catId = getUrlParameter('categoryId');
    	if(catId != undefined){
             $("#categoryList"+catId+"").addClass("categoryColor");
     	}
    	else{
   		$('#categoryAll').addClass("categoryColor");
   	   }


 });     
 function getUrlParameter(sParam)
 {
     var sPageURL = window.location.search.substring(1);
     var sURLVariables = sPageURL.split('&');
     for (var i = 0; i < sURLVariables.length; i++) 
     {
         var sParameterName = sURLVariables[i].split('=');
         if (sParameterName[0] == sParam) 
         {
             return sParameterName[1];
         }
     }
 }        

  function sortby1(){

	 var dropdown=$('#authorName').val();
	 var cal1=$('#publishDateFrom').val();
	 var cal2=$('#publishDateTo').val();
	 var minPrice=$('#minPrice').val();
	 var maxPrice=$('#maxPrice').val();

	 if(minPrice >= maxPrice){	 
		alert("Please select minprice lessthan maxprice");
		return false;
	 }

	 if((cal1=="undefined"&&cal2!="undefined")){alert("Enter From Date");}
	 else if((cal1!="undefined"&&cal2=="undefined")){alert("Enter To Date");}
	 if(dropdown!="undefined" && cal1=="undefined"){
	 myForm.action ="reportDetailList"+"?optionbox1="+dropdown;
	 }
	 else if(dropdown=="undefined"){myForm.action ="reportDetailList"+"?optionbox2="+cal1+"&optionbox3="+cal2;}
	 else if(dropdown!="undefined"&&cal1!="undefined"){myForm.action ="reportDetailList"+"?optionbox1="+dropdown+"&optionbox2="+cal1+"&optionbox3="+cal2+"&optionbox4="+minPrice+"&optionbox5="+maxPrice;}

	 document.myForm.submit() ; 
  }

  function submitForm(myfield,event){
	  var keycode;
	  if (window.event)
	  keycode = window.event.keyCode;
	  else if (event) 
	  keycode = event.which;
	  else 
	  return true;

	  if (keycode == 13){ 
      myForm.action="booksearch";	
	  document.forms.myForm.submit();
	  return false;
	  }
	  else
	  return true;

  }

