      function sortby(sel) {

    	  myForm.action ="booksearch"+"?optionbox=" + sel;
    	  document.myForm.submit() ; 

      }
      function submitForm(myfield,event){
    	  var keycode;
    	  if (window.event)
    	  keycode = window.event.keyCode;
    	  else if (event) 
    	  keycode = event.which;
    	  else 
    	  return true;

    	  if (keycode == 13){ 
    	  myForm.action="booksearch";	
    	  document.forms.myForm.submit();
    	  return false;
    	  }
    	  else
    	  return true;

      }
      function searchBook(){
    		//alert("hi");


