      $(document).ready(function(){alert('hi');
  	var url1='show_cart';
  	carts1(url1);
  });

  function getHeaderData()
  {
	  //alert('hi');
	  	var url1='show_cart';
	  	carts1(url1);
  }

  function carts1(url1){
      $.ajax({
     		cache: false,
             url:url1,
             dataType: 'json',
             success: function(data){  
             	console.log(data.cart.allItems);

             	   if(data.cart.allItems.length != 0){
             	      $('#cartBody').html('');

             	   //alert(data.cart.allItems.length);
             	$('#itemValue').append("("+data.cart.allItems.length+" items)");	

              //Price val in header
              //alert(data.cart.total);
    	  	  $('#headerPrice').html('');
    	  	  $('#headerPrice').append("$"+data.cart.total);  
             	  }else{
             		$('#itemValue').append("(0 items)");
             	 //Price val in header
        	  	  $('#headerPrice').html('');
        	  	  $('#headerPrice').append("$ 0.0");
             	  }

              },
     	});


     }

function searchBook(){
	//alert("hi");


	 var cal1=document.getElementById("input").value;
	 //alert(cal1);
	 myForm.action ="booksearch"+"?book="+cal1;
	 document.myForm.submit() ; 
 }

